
// Program to calculate the perimiter and area of a rectangle
public class HW3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
		int length = 5;
		int width = 4;
		
		// area = length * width;
		int area = length * width;
		
		// perimiter = 2 * (length + width)
		int perimiter = 2 * (length + width);
		
		System.out.println("The perimiter of a rectangle with width (" + width + ") and length (" + length + ") is " + perimiter + " and the area is " + area);
	}

}